mapper.select:
SELECT user_no, user_id, user_pw, user_email, user_tel, user_addr, user_reg, user_yn
FROM user
WHERE user_id = #{userId} AND user_pw = #{userPw}

SELECT user_no, user_id, user_pw, user_email, user_tel, user_addr, user_reg, user_yn
FROM user
WHERE user_id = #{userId}

mapper.update:
UPDATE user
SET user_pw = #{userPw},
user_email = #{userEmail},
user_tel = #{userTel},
user_addr = #{userAddr}
WHERE user_id = #{userId}

